package de.signWritingEditor.shared.badge.model.material;

import static de.signWritingEditor.shared.i18n.I18NAccess.I18N;

import java.util.Date;

import de.badge.shared.model.domainValue.BadgeLevel;
import de.badge.shared.model.domainValue.BadgeType;
import de.jsonParser.shared.infrastructure.JsonParser;
import de.signWritingEditor.shared.badge.model.domainValue.DelegsBadgeType;

public class DifferentDayLoginBadge extends DelegsBadge {

	public static final JsonBlankFactory<DifferentDayLoginBadge> JSON_BLANK_FACTORY = new JsonBlankFactory<DifferentDayLoginBadge>() {
		@Override
		public DifferentDayLoginBadge doCreateJsonBlank() {
			return new DifferentDayLoginBadge();
		}
	};

	private static final long serialVersionUID = -4306307297347224792L;
	private static final BadgeType BADGE_TYPE = DelegsBadgeType.DIFFERENT_DAY_LOGINS;

	private static final String BRONZE_BADGE_IMAGE = "data:image/png;base64,";
	private static final String SILVER_BADGE_IMAGE = "data:image/png;base64,";
	private static final String GOLD_BADGE_IMAGE = "data:image/png;base64,";

	private static final int BRONZE_REQUIRED_LOGINS = 5;
	private static final int SILVER_REQUIRED_LOGINS = 30;
	private static final int GOLD_REQUIRED_LOGINS = 100;

	private int differentDayLogins;
	private Date lastLogin;

	public DifferentDayLoginBadge(String ownername, Date dateEarned, int differentDayLogins, Date lastLogin) {
		super(ownername, BADGE_TYPE, dateEarned);
		this.differentDayLogins = differentDayLogins;
		this.lastLogin = lastLogin;

		doConsistencyCheck();
	}

	public DifferentDayLoginBadge(String ownername) {
		this(ownername, null, 0, null);
	}

	@Deprecated
	private DifferentDayLoginBadge() {
		super();
	}

	@Override
	public String getTitle() {
		return I18N.getDifferentDayLoginBadgeTitle();
	}

	@Override
	public String getDescription() {
		return I18N.getDifferentDayLoginBadgeDescription();
	}

	@Override
	public String getStatus() {
		String result = I18N.getBadgeStatusForHighestRank();

		BadgeLevel level = getCurrentLevel();
		if (level == BadgeLevel.NONE) {
			result = I18N.getDifferentDayLoginBadgeStatusForNone(getMissingLoginsForNextBadge());
		} else if (level == BadgeLevel.BRONZE) {
			result = I18N.getDifferentDayLoginBadgeStatusForBronze(getMissingLoginsForNextBadge());
		} else if (level == BadgeLevel.SILVER) {
			result = I18N.getDifferentDayLoginBadgeStatusForSilver(getMissingLoginsForNextBadge());
		}

		return result;
	}

	public boolean userLoggedIn(Date loginTime) {
		boolean newBadgeReceived = false;

		if (lastLogin == null
				|| !yearMonthAndDayFormat.format(loginTime).equals(yearMonthAndDayFormat.format(lastLogin))) {
			BadgeLevel oldLevel = getCurrentLevel();
			lastLogin = loginTime;
			differentDayLogins++;
			BadgeLevel newLevel = getCurrentLevel();
			if (newLevel != oldLevel) {
				setDateEarned(new Date());
				newBadgeReceived = true;
			}
		}
		return newBadgeReceived;
	}

	public BadgeLevel getCurrentLevel() {
		if (differentDayLogins >= GOLD_REQUIRED_LOGINS) {
			return BadgeLevel.GOLD;
		}
		if (differentDayLogins >= SILVER_REQUIRED_LOGINS) {
			return BadgeLevel.SILVER;
		}
		if (differentDayLogins >= BRONZE_REQUIRED_LOGINS) {
			return BadgeLevel.BRONZE;
		}
		return BadgeLevel.NONE;
	}

	@Override
	protected String getBronzeBadgeAsBase64() {
		return BRONZE_BADGE_IMAGE;
	}

	@Override
	protected String getSilverBadgeAsBase64() {
		return SILVER_BADGE_IMAGE;
	}

	@Override
	protected String getGoldBadgeAsBase64() {
		return GOLD_BADGE_IMAGE;
	}

	@Override
	protected void setJson(JsonParser jsonParser) {
		assert jsonParser != null : "Precondition failed: jsonParser != null";

		super.setJson(jsonParser);

		differentDayLogins = readIntFromJson(jsonParser, "differentDayLogins");
		Long longValue = readLongObjectFromJson(jsonParser, "lastLogin");
		lastLogin = longValue == null ? null : new Date(longValue);
	}

	@Override
	protected String getJson() {
		StringBuilder result = new StringBuilder(super.getJson());

		writeIntToJson(result, "differentDayLogins", differentDayLogins);
		writeLongObjectToJson(result, "lastLogin", (lastLogin != null) ? lastLogin.getTime() : null);

		assert result != null : "Postcondition failed: result != null";
		return result.toString();
	}

	private long getMissingLoginsForNextBadge() {
		BadgeLevel currentLevel = getCurrentLevel();
		assert currentLevel != BadgeLevel.GOLD : "Precondition failed: assert currentLevel != BadgeLevel.GOLD";

		long result = -1;
		switch (currentLevel) {
		case NONE:
			result = BRONZE_REQUIRED_LOGINS - differentDayLogins;
			break;
		case BRONZE:
			result = SILVER_REQUIRED_LOGINS - differentDayLogins;
			break;
		case SILVER:
			result = GOLD_REQUIRED_LOGINS - differentDayLogins;
			break;
		case GOLD:
		default:
			throw new RuntimeException("you cannot call getMissingLoginsForNextBadge() for level " + currentLevel);
		}

		assert result > 0 : "Postcondition failed: result > 0";
		return result;
	}

}
