package de.signWritingEditor.shared.badge.model.material;

import static de.signWritingEditor.shared.i18n.I18NAccess.I18N;

import java.util.Date;

import de.badge.shared.model.domainValue.BadgeLevel;
import de.jsonParser.shared.infrastructure.JsonParser;
import de.signWritingEditor.shared.badge.model.domainValue.DelegsBadgeType;

public class DocumentCreatorBadge extends DelegsBadge {

	public static final JsonBlankFactory<DocumentCreatorBadge> JSON_BLANK_FACTORY = new JsonBlankFactory<DocumentCreatorBadge>() {
		@Override
		public DocumentCreatorBadge doCreateJsonBlank() {
			return new DocumentCreatorBadge();
		}
	};

	private static final long serialVersionUID = -8437748263866185024L;
	private static final DelegsBadgeType BADGE_TYPE = DelegsBadgeType.DOCUMENT_CREATOR;

	private static final String BRONZE_BADGE_IMAGE = "data:image/png;base64,";
	private static final String SILVER_BADGE_IMAGE = "data:image/png;base64,";
	private static final String GOLD_BADGE_IMAGE = "data:image/png;base64,";

	private static final int BRONZE_REQUIRED_DOCUMENTS = 3;
	private static final int SILVER_REQUIRED_DOCUMENTS = 10;
	private static final int GOLD_REQUIRED_DOCUMENTS = 45;

	private int documentsCreated;

	public DocumentCreatorBadge(String ownername, Date dateEarned, int documentsCreated) {
		super(ownername, BADGE_TYPE, dateEarned);
		this.documentsCreated = documentsCreated;

		doConsistencyCheck();
	}

	public DocumentCreatorBadge(String ownername) {
		this(ownername, null, 0);
	}

	@Deprecated
	private DocumentCreatorBadge() {
		super();
	}

	@Override
	public String getTitle() {
		return I18N.getDocumentCreatorBadgeTitle();
	}

	@Override
	public String getDescription() {
		return I18N.getDocumentCreatorBadgeDescription();
	}

	@Override
	public String getStatus() {
		String result = I18N.getBadgeStatusForHighestRank();

		BadgeLevel level = getCurrentLevel();
		if (level == BadgeLevel.NONE) {
			result = I18N.getDocumentCreatorBadgeStatusForNone(getMissingDocumentsForNextBadge());
		} else if (level == BadgeLevel.BRONZE) {
			result = I18N.getDocumentCreatorBadgeStatusForBronze(getMissingDocumentsForNextBadge());
		} else if (level == BadgeLevel.SILVER) {
			result = I18N.getDocumentCreatorBadgeStatusForSilver(getMissingDocumentsForNextBadge());
		}

		return result;
	}

	public boolean userCreatedDocument() {
		boolean newBadgeReceived = false;

		BadgeLevel oldLevel = getCurrentLevel();
		documentsCreated++;
		BadgeLevel newLevel = getCurrentLevel();
		if (newLevel != oldLevel) {
			setDateEarned(new Date());
			newBadgeReceived = true;
		}

		return newBadgeReceived;
	}

	@Override
	public BadgeLevel getCurrentLevel() {
		if (documentsCreated >= GOLD_REQUIRED_DOCUMENTS) {
			return BadgeLevel.GOLD;
		}
		if (documentsCreated >= SILVER_REQUIRED_DOCUMENTS) {
			return BadgeLevel.SILVER;
		}
		if (documentsCreated >= BRONZE_REQUIRED_DOCUMENTS) {
			return BadgeLevel.BRONZE;
		}
		return BadgeLevel.NONE;
	}

	@Override
	protected String getBronzeBadgeAsBase64() {
		return BRONZE_BADGE_IMAGE;
	}

	@Override
	protected String getSilverBadgeAsBase64() {
		return SILVER_BADGE_IMAGE;
	}

	@Override
	protected String getGoldBadgeAsBase64() {
		return GOLD_BADGE_IMAGE;
	}

	@Override
	protected void setJson(JsonParser jsonParser) {
		assert jsonParser != null : "Precondition failed: jsonParser != null";

		super.setJson(jsonParser);

		documentsCreated = readIntFromJson(jsonParser, "documentsCreated");
	}

	@Override
	protected String getJson() {
		StringBuilder result = new StringBuilder(super.getJson());

		writeIntToJson(result, "documentsCreated", documentsCreated);

		assert result != null : "Postcondition failed: result != null";
		return result.toString();
	}

	private long getMissingDocumentsForNextBadge() {
		BadgeLevel currentLevel = getCurrentLevel();
		assert currentLevel != BadgeLevel.GOLD : "Precondition failed: assert currentLevel != BadgeLevel.GOLD";

		long result = -1;
		switch (currentLevel) {
		case NONE:
			result = BRONZE_REQUIRED_DOCUMENTS - documentsCreated;
			break;
		case BRONZE:
			result = SILVER_REQUIRED_DOCUMENTS - documentsCreated;
			break;
		case SILVER:
			result = GOLD_REQUIRED_DOCUMENTS - documentsCreated;
			break;
		case GOLD:
		default:
			throw new RuntimeException("you cannot call getMissingDocumentsForNextBadge() for level " + currentLevel);
		}

		assert result > 0 : "Postcondition failed: result > 0";
		return result;
	}

}
