package de.signWritingEditor.shared.badge.model.material;

import static de.signWritingEditor.shared.i18n.I18NAccess.I18N;

import java.util.Date;

import de.badge.shared.model.domainValue.BadgeLevel;
import de.jsonParser.shared.infrastructure.JsonParser;
import de.signWritingEditor.shared.badge.model.domainValue.DelegsBadgeType;

public class LongTimeUserBadge extends DelegsBadge {

	public static final JsonBlankFactory<LongTimeUserBadge> JSON_BLANK_FACTORY = new JsonBlankFactory<LongTimeUserBadge>() {
		@Override
		public LongTimeUserBadge doCreateJsonBlank() {
			return new LongTimeUserBadge();
		}
	};

	private static final long serialVersionUID = -7890561121264874745L;
	private static final DelegsBadgeType BADGE_TYPE = DelegsBadgeType.LONG_TIME_USER;

	private static final String BRONZE_BADGE_IMAGE = "data:image/png;base64,";
	private static final String SILVER_BADGE_IMAGE = "data:image/png;base64,";
	private static final String GOLD_BADGE_IMAGE = "data:image/png;base64,";

	private static final long MS_IN_A_DAY = 24 * 60 * 60 * 1000;
	private static final int BRONZE_REQUIRED_DAYS = 21;
	private static final int SILVER_REQUIRED_DAYS = 90;
	private static final int GOLD_REQUIRED_DAYS = 365;

	private Date signUp;

	public LongTimeUserBadge(String ownername, Date dateEarned, Date signUp) {
		super(ownername, BADGE_TYPE, dateEarned);
		this.signUp = signUp;

		doConsistencyCheck();
	}

	public LongTimeUserBadge(String ownername) {
		this(ownername, null, new Date());
	}

	@Deprecated
	private LongTimeUserBadge() {
		super();
	}

	@Override
	public String getTitle() {
		return I18N.getLongTimeUserBadgeTitle();
	}

	@Override
	public String getDescription() {
		return I18N.getLongTimeUserBadgeDescription();
	}

	@Override
	public String getStatus() {
		String result = I18N.getBadgeStatusForHighestRank();

		BadgeLevel level = getCurrentLevel();
		if (level == BadgeLevel.NONE) {
			result = I18N.getLongTimeUserBadgeStatusForNone(getMissingDaysForNextBadge());
		} else if (level == BadgeLevel.BRONZE) {
			result = I18N.getLongTimeUserBadgeStatusForBronze(getMissingDaysForNextBadge());
		} else if (level == BadgeLevel.SILVER) {
			result = I18N.getLongTimeUserBadgeStatusForSilver(getMissingDaysForNextBadge());
		}

		return result;
	}

	@Override
	public BadgeLevel getCurrentLevel() {
		long userForDays = (System.currentTimeMillis() - signUp.getTime()) / MS_IN_A_DAY;
		if (userForDays >= 365) {
			long timeEarned = signUp.getTime() + (BRONZE_REQUIRED_DAYS * MS_IN_A_DAY);
			setDateEarned(new Date(timeEarned));
			return BadgeLevel.GOLD;
		}
		if (userForDays >= 90) {
			long timeEarned = signUp.getTime() + (SILVER_REQUIRED_DAYS * MS_IN_A_DAY);
			setDateEarned(new Date(timeEarned));
			return BadgeLevel.SILVER;
		}
		if (userForDays >= 21) {
			long timeEarned = signUp.getTime() + (BRONZE_REQUIRED_DAYS * MS_IN_A_DAY);
			setDateEarned(new Date(timeEarned));
			return BadgeLevel.BRONZE;
		}
		return BadgeLevel.NONE;
	}

	@Override
	protected String getBronzeBadgeAsBase64() {
		return BRONZE_BADGE_IMAGE;
	}

	@Override
	protected String getSilverBadgeAsBase64() {
		return SILVER_BADGE_IMAGE;
	}

	@Override
	protected String getGoldBadgeAsBase64() {
		return GOLD_BADGE_IMAGE;
	}

	@Override
	protected void setJson(JsonParser jsonParser) {
		assert jsonParser != null : "Precondition failed: jsonParser != null";

		super.setJson(jsonParser);

		Long longValue = readLongObjectFromJson(jsonParser, "signUp");
		signUp = longValue == null ? null : new Date(longValue);
	}

	@Override
	protected String getJson() {
		StringBuilder result = new StringBuilder(super.getJson());

		writeLongObjectToJson(result, "signUp", signUp == null ? null : signUp.getTime());

		assert result != null : "Postcondition failed: result != null";
		return result.toString();
	}

	private long getMissingDaysForNextBadge() {
		BadgeLevel currentLevel = getCurrentLevel();
		assert currentLevel != BadgeLevel.GOLD : "Precondition failed: assert currentLevel != BadgeLevel.GOLD";

		long userForDays = (System.currentTimeMillis() - signUp.getTime()) / MS_IN_A_DAY;
		long result = -1;
		switch (currentLevel) {
		case NONE:
			result = BRONZE_REQUIRED_DAYS - userForDays;
			break;
		case BRONZE:
			result = SILVER_REQUIRED_DAYS - userForDays;
			break;
		case SILVER:
			result = GOLD_REQUIRED_DAYS - userForDays;
			break;
		case GOLD:
		default:
			throw new RuntimeException("you cannot call getMissingDaysForNextBadge() for level " + currentLevel);
		}

		assert result > 0 : "Postcondition failed: result > 0";
		return result;
	}

}
