package de.signWritingEditor.shared.badge.model.material;

import static de.signWritingEditor.shared.i18n.I18NAccess.I18N;

import java.util.Date;

import de.badge.shared.model.domainValue.BadgeLevel;
import de.jsonParser.shared.infrastructure.JsonParser;
import de.signWritingEditor.shared.badge.model.domainValue.DelegsBadgeType;

public class NoviceBadge extends DelegsBadge {

	public static final JsonBlankFactory<NoviceBadge> JSON_BLANK_FACTORY = new JsonBlankFactory<NoviceBadge>() {
		@Override
		public NoviceBadge doCreateJsonBlank() {
			return new NoviceBadge();
		}
	};

	private static final long serialVersionUID = 3885553851876047086L;
	private static final DelegsBadgeType BADGE_TYPE = DelegsBadgeType.NOVICE;

	private static final String BRONZE_BADGE_IMAGE = "data:image/png;base64,";
	private static final String SILVER_BADGE_IMAGE = "data:image/png;base64,";
	private static final String GOLD_BADGE_IMAGE = "data:image/png;base64,";

	private boolean createdDocument;
	private boolean openedOwnDocument;
	private boolean openedSignEditor;

	public NoviceBadge(String ownername, Date dateEarned, boolean createdDocument, boolean openedOwnDocument,
			boolean openedSignEditor) {
		super(ownername, BADGE_TYPE, dateEarned);
		this.createdDocument = createdDocument;
		this.openedOwnDocument = openedOwnDocument;
		this.openedSignEditor = openedSignEditor;

		doConsistencyCheck();
	}

	public NoviceBadge(String ownername) {
		this(ownername, null, false, false, false);
	}

	@Deprecated
	private NoviceBadge() {
		super();
	}

	@Override
	public String getTitle() {
		return I18N.getNoviceBadgeTitle();
	}

	@Override
	public String getDescription() {
		return I18N.getNoviceBadgeDescription();
	}

	@Override
	public String getStatus() {
		String result = I18N.getBadgeStatusForHighestRank();

		BadgeLevel level = getCurrentLevel();
		if (level == BadgeLevel.NONE) {
			result = I18N.getNoviceBadgeStatusForNone();
		} else if (level == BadgeLevel.BRONZE) {
			result = I18N.getNoviceBadgeStatusForBronze();
		} else if (level == BadgeLevel.SILVER) {
			result = I18N.getNoviceBadgeStatusForSilver();
		}

		return result;
	}

	public BadgeLevel getCurrentLevel() {
		if (createdDocument && openedOwnDocument && openedSignEditor) {
			return BadgeLevel.GOLD;
		}
		if (createdDocument && openedOwnDocument) {
			return BadgeLevel.SILVER;
		}
		if (createdDocument) {
			return BadgeLevel.BRONZE;
		}
		return BadgeLevel.NONE;
	}

	public boolean userCreatedDocument() {
		boolean newBadgeReceived = false;

		BadgeLevel oldLevel = getCurrentLevel();
		createdDocument = true;
		BadgeLevel newLevel = getCurrentLevel();
		if (newLevel != oldLevel) {
			setDateEarned(new Date());
			newBadgeReceived = true;
		}

		return newBadgeReceived;
	}

	public boolean userOpenedOwnDocument() {
		boolean newBadgeReceived = false;

		BadgeLevel oldLevel = getCurrentLevel();
		openedOwnDocument = true;
		BadgeLevel newLevel = getCurrentLevel();
		if (newLevel != oldLevel) {
			setDateEarned(new Date());
			newBadgeReceived = true;
		}

		return newBadgeReceived;
	}

	public boolean userOpenedSignEditor() {
		boolean newBadgeReceived = false;

		BadgeLevel oldLevel = getCurrentLevel();
		openedSignEditor = true;
		BadgeLevel newLevel = getCurrentLevel();
		if (newLevel != oldLevel) {
			setDateEarned(new Date());
			newBadgeReceived = true;
		}

		return newBadgeReceived;
	}

	@Override
	protected String getBronzeBadgeAsBase64() {
		return BRONZE_BADGE_IMAGE;
	}

	@Override
	protected String getSilverBadgeAsBase64() {
		return SILVER_BADGE_IMAGE;
	}

	@Override
	protected String getGoldBadgeAsBase64() {
		return GOLD_BADGE_IMAGE;
	}

	@Override
	protected void setJson(JsonParser jsonParser) {
		assert jsonParser != null : "Precondition failed: jsonParser != null";

		super.setJson(jsonParser);

		createdDocument = readBooleanFromJson(jsonParser, "createdDocument");
		openedOwnDocument = readBooleanFromJson(jsonParser, "openedOwnDocument");
		openedSignEditor = readBooleanFromJson(jsonParser, "openedSignEditor");
	}

	@Override
	protected String getJson() {
		StringBuilder result = new StringBuilder(super.getJson());

		writeBooleanToJson(result, "createdDocument", createdDocument);
		writeBooleanToJson(result, "openedOwnDocument", openedOwnDocument);
		writeBooleanToJson(result, "openedSignEditor", openedSignEditor);

		assert result != null : "Postcondition failed: result != null";
		return result.toString();
	}

}
